INSERT INTO `hi_posts` VALUES ('38', '1', '', '', '2018-04-02 16:07:51', '<p dir=\"ltr\" style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\"><br/></span></p><p dir=\"ltr\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\"><br/></span></p><p dir=\"ltr\" style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">在这里</span></p><p dir=\"ltr\" style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">没有待人接物般厚重的仪式感</span></p><p dir=\"ltr\" style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">只有回归家庭后的闲适自得</span></p><p><br style=\"text-align: left;\"/></p><p style=\"text-align: center;\"><img src=\"/data/upload/ueditor/20180402/5ac1e513abd6d.JPG\" title=\"1.JPG\" alt=\"1.JPG\" width=\"748\" height=\"1030\" style=\"width: 748px; height: 1030px;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\"><br/></span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">干净有力的线条</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">划破了空间的宁静</span></p><p><br style=\"text-align: left;\"/></p><p style=\"text-align: center;\"><img src=\"/data/upload/ueditor/20180402/5ac1e52130e01.JPG\" title=\"2.JPG\" alt=\"2.JPG\" width=\"744\" height=\"1091\" style=\"color: rgb(136, 136, 136); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 12px; letter-spacing: 2px; text-align: center; width: 744px; height: 1091px;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\"><br/></span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">越简约越能看清事物的本真</span></p><p><br style=\"text-align: left;\"/></p><p style=\"text-align: center;\"><img src=\"/data/upload/ueditor/20180402/5ac1e52e36fa2.JPG\" style=\"width: 750px; height: 509px;\" title=\"4.JPG\" width=\"750\" height=\"509\"/></p><p style=\"text-align: center;\"><br/></p><p><img src=\"/data/upload/ueditor/20180402/5ac1e52e391d2.JPG\" style=\"width: 747px; height: 511px;\" title=\"5.JPG\" width=\"747\" height=\"511\"/></p><p style=\"text-align: center;\"><br/></p><p><img src=\"/data/upload/ueditor/20180402/5ac1e52e4280e.JPG\" style=\"width: 747px; height: 523px;\" title=\"3.JPG\" width=\"747\" height=\"523\"/></p><p style=\"text-align: center;\"><br/></p><p><img src=\"/data/upload/ueditor/20180402/5ac1e54a60a61.JPG\" title=\"6.JPG\" alt=\"6.JPG\" width=\"744\" height=\"491\" style=\"width: 744px; height: 491px;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\"><br/></span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">因空间高度的需求，向地下挖了一米的深度</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">才有了现在的层高</span></p><p><br style=\"text-align: left;\"/></p><p style=\"text-align: center;\"><img src=\"/data/upload/ueditor/20180402/5ac1e54f84778.JPG\" title=\"7.JPG\" alt=\"7.JPG\" width=\"748\" height=\"552\" style=\"width: 748px; height: 552px;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\"><br/></span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">办公室一角</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">一直在做设计</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">有时候需要安静的坐下来思考初心</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">时刻告诫自己</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">向内求</span></p><p><br style=\"text-align: left;\"/></p><p style=\"text-align: center;\"><img src=\"/data/upload/ueditor/20180402/5ac1e55425805.JPG\" title=\"8.JPG\" alt=\"8.JPG\" width=\"746\" height=\"598\" style=\"width: 746px; height: 598px;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\"><br/></span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">空间也是自然环境的一部分</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">需要人来滋养，与之产生联系</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">一个好的空间是需要一个好的主人慢慢养出来的<br style=\"margin: 0px; padding: 0px; text-align: left;\"/></span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">一开始它是没有生命的</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">相处久了</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">这才有了家的温度</span></p><p><br style=\"text-align: left;\"/></p><p style=\"text-align: center;\"><img src=\"/data/upload/ueditor/20180402/5ac1e558dfd14.JPG\" title=\"9.JPG\" alt=\"9.JPG\" width=\"746\" height=\"473\" style=\"width: 746px; height: 473px;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\"><br/></span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">空间是黑白分明的</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">这也代表着我们做事的态度</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">简单明了</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">始终如一</span></p><p><br style=\"text-align: left;\"/></p><p style=\"text-align: center;\"><img src=\"/data/upload/ueditor/20180402/5ac1e55ccf5b2.JPG\" title=\"10.JPG\" alt=\"10.JPG\" width=\"738\" height=\"1101\" style=\"width: 738px; height: 1101px;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\"><br/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\">空间是服务人的，在黑白的世界里，</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\">人的肤色就是我们最丰富的色彩</span></p><p><br style=\"text-align: left;\"/></p><p style=\"text-align: center;\"><img src=\"/data/upload/ueditor/20180402/5ac1e561afe1c.JPG\" title=\"11.JPG\" alt=\"11.JPG\" width=\"745\" height=\"517\" style=\"width: 745px; height: 517px;\"/></p><p style=\"text-align: center;\"><br/></p><p><img src=\"/data/upload/ueditor/20180402/5ac1e565d3afa.JPG\" title=\"12.JPG\" alt=\"12.JPG\" width=\"751\" height=\"559\" style=\"width: 751px; height: 559px;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\"><br/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\">我们在公司的中心部分</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\">设计了一处榻榻米，取名（之间）</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\">情理之间</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\">黑白之间</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\">人与人之间</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\">天地之间</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\">都可以在这里被释怀</span></p><p><br style=\"text-align: left;\"/></p><p style=\"text-align: center;\"><img src=\"/data/upload/ueditor/20180402/5ac1e56a341ec.JPG\" title=\"13.JPG\" alt=\"13.JPG\" width=\"746\" height=\"449\" style=\"width: 746px; height: 449px;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\"><br/></span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">未来不知在哪，但我们已在路上</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">走一条寻求内在的设计道路</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">做一家知冷暖的设计公司</span></p><p><br style=\"text-align: left;\"/></p><p style=\"text-align: center;\"><img src=\"/data/upload/ueditor/20180402/5ac1e56e388a0.JPG\" title=\"14.JPG\" alt=\"14.JPG\" width=\"747\" height=\"1162\" style=\"width: 747px; height: 1162px;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; color: rgb(165, 165, 165);\"><br/></span></p><p style=\"text-align: left;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">项目名称：東形西見设计机构<br style=\"margin: 0px; padding: 0px; text-align: left;\"/></span></p><p style=\"text-align: left;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">项目地点：福建 福州&nbsp;</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">主持设计：李超 陈志曙</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">设计团队：江伟 朱毅 吴圣辉</span></p><p style=\"text-align: left;\"><span style=\"color: rgb(165, 165, 165); font-size: 12px;\">摄影丨吴永长</span></p><p><br style=\"text-align: left;\"/></p><hr style=\"margin: 0px; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: medium; white-space: normal; line-height: 25.6px;\"/><p><br style=\"margin: 0px; padding: 0px; text-align: left;\"/></p><p style=\"text-align: center;\"><img src=\"/data/upload/ueditor/20180402/5ac1e5774ec9d.jpg\" title=\"640.webp.jpg\" alt=\"640.webp.jpg\" width=\"751\" height=\"1682\" style=\"width: 751px; height: 1682px;\"/></p>', '无问东西', '未来不知在哪，但我们已在路上', '1', '1', '2018-04-13 14:24:19